import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { guessLetter, resetGame, fetchWordsRequest, fetchWordsSuccess, fetchWordsFailure } from './redux/actions';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import axios from 'axios';

const App = () => {
  const dispatch = useDispatch();
  const { word, guessedLetters, incorrectGuesses, maxGuesses, loading, error } = useSelector((state) => state);

  useEffect(() => {
    const fetchWords = async () => {
      dispatch(fetchWordsRequest());
      try {
        // Using WordAPI
        const response = await axios.get('https://random-word-api.herokuapp.com/word?number=10');
        dispatch(fetchWordsSuccess(response.data));
      } catch (err) {
        console.error('Error details:', err);
        dispatch(fetchWordsFailure(err.message || 'Failed to fetch words'));
      }
    };

    fetchWords();
  }, [dispatch]);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      const letter = e.target.value.toUpperCase().trim();
      console.log(`Letter guessed: ${letter}`); // Debug log
      if (letter.length === 1 && /^[A-Z]$/.test(letter)) { // Ensure input is a single letter
        dispatch(guessLetter(letter));
      }
      e.target.value = ''; // Clear input field
    }
  };

  const handleReset = () => {
    dispatch(resetGame());
  };

  if (loading) return <p>Loading words...</p>;
  if (error) return <p>Error fetching words: {error}</p>;

  const displayWord = word
    .split('')
    .map((letter) => (guessedLetters.includes(letter) ? letter : '_'))
    .join(' ');

  return (
    <div className="App">
      <h1>Hangman Game</h1>
      <AnimatePresence>
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 20 }}
          transition={{ duration: 0.5 }}
        >
          <h2>Word: {displayWord}</h2>
        </motion.div>
      </AnimatePresence>

      <AnimatePresence>
        <motion.p
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          Incorrect Guesses: {incorrectGuesses}/{maxGuesses}
        </motion.p>
      </AnimatePresence>

      {incorrectGuesses >= maxGuesses && (
        <AnimatePresence>
          <motion.h2
            initial={{ opacity: 0, y: -30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 30 }}
            transition={{ duration: 0.5 }}
          >
            Game Over
          </motion.h2>
        </AnimatePresence>
      )}

      <div>
        <motion.input
          type="text"
          maxLength="1"
          onKeyDown={handleKeyDown} // Use onKeyDown
          placeholder="Guess a letter"
          initial={{ scale: 1 }}
          whileFocus={{ scale: 1.1 }}
          whileHover={{ scale: 1.1 }}
          transition={{ type: 'spring', stiffness: 300 }}
        />
        <motion.button
          onClick={handleReset}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          transition={{ type: 'spring', stiffness: 300 }}
        >
          Reset Game
        </motion.button>
      </div>
    </div>
  );
};

export default App;
