// src/redux/actions.js
import axios from 'axios';

export const SET_WORDS = 'SET_WORDS';
export const SET_NEXT_WORD = 'SET_NEXT_WORD';
export const FETCH_WORDS_REQUEST = 'FETCH_WORDS_REQUEST';
export const FETCH_WORDS_SUCCESS = 'FETCH_WORDS_SUCCESS';
export const FETCH_WORDS_FAILURE = 'FETCH_WORDS_FAILURE';

export const fetchWords = () => async (dispatch) => {
  dispatch({ type: FETCH_WORDS_REQUEST });
  try {
    // Replace with the URL of the API you are using
    const response = await axios.get('https://api.example.com/words');
    dispatch({ type: FETCH_WORDS_SUCCESS, payload: response.data.words });
  } catch (error) {
    dispatch({ type: FETCH_WORDS_FAILURE, payload: error.message });
  }
};

export const setNextWord = () => ({
  type: SET_NEXT_WORD,
});
