// src/App.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { guessLetter, resetGame } from './redux/actions';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';

const App = () => {
  const { word, guessedLetters, incorrectGuesses, maxGuesses } = useSelector((state) => state);
  const dispatch = useDispatch();

  const handleGuess = (e) => {
    if (e.key === 'Enter') {
      const letter = e.target.value.toUpperCase();
      dispatch(guessLetter(letter));
      e.target.value = ''; // Clear input field
    }
  };

  const handleReset = () => {
    dispatch(resetGame());
  };

  const displayWord = word
    .split('')
    .map((letter) => (guessedLetters.includes(letter) ? letter : '_'))
    .join(' ');

  return (
    <div className="App">
      <h1>Hangman Game</h1>
      <AnimatePresence>
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 20 }}
          transition={{ duration: 0.5 }}
        >
          <h2>Word: {displayWord}</h2>
        </motion.div>
      </AnimatePresence>

      <AnimatePresence>
        <motion.p
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          Incorrect Guesses: {incorrectGuesses}/{maxGuesses}
        </motion.p>
      </AnimatePresence>

      {incorrectGuesses >= maxGuesses && (
        <AnimatePresence>
          <motion.h2
            initial={{ opacity: 0, y: -30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 30 }}
            transition={{ duration: 0.5 }}
          >
            Game Over
          </motion.h2>
        </AnimatePresence>
      )}

      <div>
        <motion.input
          type="text"
          maxLength="1"
          onKeyPress={handleGuess}
          placeholder="Guess a letter"
          initial={{ scale: 1 }}
          whileFocus={{ scale: 1.1 }}
          whileHover={{ scale: 1.1 }}
          transition={{ type: 'spring', stiffness: 300 }}
        />
        <motion.button
          onClick={handleReset}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          transition={{ type: 'spring', stiffness: 300 }}
        >
          Reset Game
        </motion.button>
      </div>
    </div>
  );
};

export default App;
