import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { guessLetter, resetGame } from './redux/actions';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import axios from 'axios';

const App = () => {
  const dispatch = useDispatch();
  const { word, guessedLetters, incorrectGuesses, maxGuesses } = useSelector((state) => state);

  useEffect(() => {
    const fetchWords = async () => {
      dispatch(fetchWordsRequest());
      try {
        const response = await axios.get('https://random-word-api.herokuapp.com/word?number=10');
        dispatch(fetchWordsSuccess(response.data));
      } catch (err) {
        console.error('Error details:', err);
        dispatch(fetchWordsFailure(err.message || 'Failed to fetch words'));
      }
    };

    fetchWords();
  }, [dispatch]);

  const handleGuess = (letter) => {
    dispatch(guessLetter(letter));
  };

  const handleReset = () => {
    dispatch(resetGame());
  };

  const displayWord = word
    .split('')
    .map((letter) => (guessedLetters.includes(letter) ? letter : '_'))
    .join(' ');

  // Define the keyboard layout
  const rows = [
    'QWERTYUIOP',
    'ASDFGHJKL',
    'ZXCVBNM'
  ];

  return (
    <div className="App">
      <h1>Hangman Game</h1>
      <AnimatePresence>
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 20 }}
          transition={{ duration: 0.5 }}
        >
          <h2>Word: {displayWord}</h2>
        </motion.div>
      </AnimatePresence>

      <AnimatePresence>
        <motion.p
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          Incorrect Guesses: {incorrectGuesses}/{maxGuesses}
        </motion.p>
      </AnimatePresence>

      {incorrectGuesses >= maxGuesses && (
        <AnimatePresence>
          <motion.h2
            initial={{ opacity: 0, y: -30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 30 }}
            transition={{ duration: 0.5 }}
          >
            Game Over
          </motion.h2>
        </AnimatePresence>
      )}

      <div className="keyboard">
        {rows.map((row, rowIndex) => (
          <div key={rowIndex} className="row">
            {row.split('').map((letter) => (
              <button
                key={letter}
                className="key"
                onClick={() => handleGuess(letter)}
              >
                {letter}
              </button>
            ))}
          </div>
        ))}
      </div>

      <motion.button
        onClick={handleReset}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        transition={{ type: 'spring', stiffness: 300 }}
      >
        Reset Game
      </motion.button>
    </div>
  );
};

export default App;
