// src/redux/actions.js
import axios from 'axios';

// Action Types
export const GUESS_LETTER = 'GUESS_LETTER';
export const RESET_GAME = 'RESET_GAME';
export const SET_NEXT_WORD = 'SET_NEXT_WORD';
export const FETCH_WORDS_REQUEST = 'FETCH_WORDS_REQUEST';
export const FETCH_WORDS_SUCCESS = 'FETCH_WORDS_SUCCESS';
export const FETCH_WORDS_FAILURE = 'FETCH_WORDS_FAILURE';

// Action Creators
export const guessLetter = (letter) => ({
  type: GUESS_LETTER,
  payload: letter,
});

export const resetGame = () => ({
  type: RESET_GAME,
});

export const setNextWord = () => ({
  type: SET_NEXT_WORD,
});

// Actions for fetching words
export const fetchWordsRequest = () => ({
  type: FETCH_WORDS_REQUEST,
});

export const fetchWordsSuccess = (words) => ({
  type: FETCH_WORDS_SUCCESS,
  payload: words,
});

export const fetchWordsFailure = (error) => ({
  type: FETCH_WORDS_FAILURE,
  payload: error,
});
