// src/redux/reducers.js
import { GUESS_LETTER, RESET_GAME } from './actions';

const initialState = {
  word: 'REACT',
  guessedLetters: [],
  incorrectGuesses: 0,
  maxGuesses: 6,
};

const hangmanReducer = (state = initialState, action) => {
  switch (action.type) {
    case GUESS_LETTER:
      const letter = action.payload;
      const isCorrect = state.word.includes(letter);
      const guessedLetters = [...state.guessedLetters, letter];
      const incorrectGuesses = isCorrect
        ? state.incorrectGuesses
        : state.incorrectGuesses + 1;
      return {
        ...state,
        guessedLetters,
        incorrectGuesses,
      };
    case RESET_GAME:
      return initialState;
    default:
      return state;
  }
};

export default hangmanReducer;
