// src/redux/actions.js
export const GUESS_LETTER = 'GUESS_LETTER';
export const RESET_GAME = 'RESET_GAME';

export const guessLetter = (letter) => ({
  type: GUESS_LETTER,
  payload: letter,
});

export const resetGame = () => ({
  type: RESET_GAME,
});
